INSERT INTO `quizzes` (`title`, `description`, `slug`, `image`, `lang_id`, `created_at`, `updated_at`) VALUES
('Профориентационные игры для детей дошкольного возраста', 'Период от рождения до школы, по признанию специалистов всего мира, возрастом наиболее стремительного развития ребёнка, в этот период закладываются основные физические и психические качества и свойства, делающие ребёнка ЧЕЛОВЕКОМ. Важность дошкольного возраст является основой для усвоения знаний, умений и развития познавательных интересов человека.', 'proforientacionnye-igry-dlya-detey-doshkolnogo-vozrasta', 'a5EUgmNGIW.jpg', 2, NULL, NULL),
('Тестовая система для школьников 5-8 классов', 'Тестовый комплекс предназначен для раннего определения профессиональной направленности школьников среднего звена, учитывающей оценку интересов и склонностей, способностей, мотивации, ориентацию на генеалогию профессий в семье и успеваемость для объективного выбора профиля обучения в старшем звене школы.', 'testovaya-sistema-dlya-shkolnikov-5-8-klassov', 'iuyphOM2N7.jpg', 2, NULL, NULL),
('Тестовая система для школьников 9-11 классов, выпускников, абитуриентов', 'Тестовый комплект предназначен для комплексной диагностики профессиональной направленности старшеклассников, выпускников и абитуриентов, с учетом оценки склонностей, способностей, мотивации, объективных показателей успеваемости и медицинских противопоказаний к выбору в ряде профессий.', 'testovaya-sistema-dlya-shkolnikov-9-11-klassov-vypusknikov-abiturientov', 'zsebraT8gJ.jpg', 2, NULL, NULL),
('Тест для для взрослых с целью профессионального перепрофилирования', 'Тестовый комплекс предназначен для выбора профессии в ситуации получения второго образования или перепрофилирования. Оценка происходит с учетом желания и готовности выполнять ряд профессиональных задач и действий, напрямую связанных с той или иной профессией, и отраслью. Выбор происходит с учетом мотивации и отсутствия явных медицинских противопоказаний к работе.', 'test-dlya-dlya-vzroslyh-s-celyu-professionalnogo-pereprofilirovaniya', 'CimocSr14Z.jpg', 2, NULL, NULL),
('Vocational guidance games for preschool children', 'The period from birth to school, according to experts around the world, the age of the most rapid development of the child, during this period laid the basic physical and mental qualities and properties that make the child a MAN. The importance of preschool age is the basis for the assimilation of knowledge, skills and the development of human cognitive interests.', 'vocational-guidance-games-for-preschool-children', 'zsebraT8gJ.jpg', 3, NULL, NULL),
('Test system for schoolchildren of 5-8 grades', 'The test complex is designed for early determination of the professional orientation of middle school students, taking into account the assessment of interests and aptitudes, abilities, motivation, orientation to the genealogy of professions in the family and academic performance for an objective selection of the profile of education in the senior level of school.', 'test-system-for-schoolchildren-of-5-8-grades', 'iuyphOM2N7.jpg', 3, NULL, NULL),
('The test system for students in grades 9-11, graduates, enrollee', 'The test kit is designed for comprehensive diagnosis of the professional orientation of high school students, graduates and applicants, taking into account the assessment of aptitudes, abilities, motivation, objective indicators of academic performance and medical contraindications to choose in a number of professions.', 'the-test-system-for-students-in-grades-9-11-graduates-enrollee', 'a5EUgmNGIW.jpg', 3, NULL, NULL),
('The test for adults for the purpose of professional conversion', 'The test complex is designed to choose a profession in a situation of obtaining a second education or conversion. Evaluation takes into account the willingness and readiness to perform a number of professional tasks and actions directly related to a particular profession and industry. The choice is based on the motivation and the lack of obvious medical contraindications to work.', 'the-test-for-adults-for-the-purpose-of-professional-conversion', 'CimocSr14Z.jpg', 3, NULL, NULL),
('Мектеп жасына дейінгі балаларға арналған кәсіби бағдарлық ойындар', 'Әлемдегі мамандардың пікірінше, бала туғаннан мектепке дейінгі кезең баланың ең жылдам дамуы жасында осы кезеңде баланы МАН-ты құрайтын негізгі физикалық және психикалық қасиеттер мен қасиеттерге ие болды. Мектепке дейінгі жастағы білімнің маңыздылығы - адамның танымдық мүдделерін білу, білу және дамыту.', 'mektep-zhasyna-deyingi-balalarg-a-arnalg-an-k-sibi-bag-darlyk-oyyndar', 'a5EUgmNGIW.jpg', 1, NULL, NULL),
('5-8 сынып оқушылары үшін тестілеу жүйесі', 'Сынақ кешені орта мектептің оқушыларының кәсіптік бағдарлануын ерте анықтауға, сонымен қатар қызығушылықты бағалауға, қабілеттерге, ынталандыруға, отбасындағы мамандықтардың генеалогиясына бағдарлануына және мектептің жоғарғы бөлімінде білім беру профилін объективті таңдау үшін академиялық көрсеткіштерге негізделген.', '5-8-synyp-ok-ushylary-ushin-testileu-zhuyesi', 'iuyphOM2N7.jpg', 1, NULL, NULL),
('9-11 сынып оқушылары, түлектер мен ізденушілерге арналған тестілеу жүйесі', 'Тест жиынтығы көптеген кәсіптерді таңдау үшін академиялық көрсеткіштердің және медициналық көрсеткіштердің мүмкіндіктерін, қабілеттерін, мотивациясын бағалауды ескере отырып, орта мектеп оқушыларының, түлектердің және үміткерлердің кәсіби бағдарлануын кешенді диагностикалауға арналған.', '9-11-synyp-ok-ushylary-tulekter-men-izdenushilerge-arnalg-an-testileu-zhuyesi', 'zsebraT8gJ.jpg', 1, NULL, NULL),
('Кәсіби айналдыру мақсатында ересектерге арналған тест', 'Сынақ кешені екінші білім алу немесе конверсия алу жағдайында мамандықты таңдауға арналған. Бағалау нақты мамандық пен өнеркәсіпке тікелей қатысы бар бірқатар кәсіби міндеттер мен іс-әрекеттерді жасауға дайындықты және дайындықты ескереді. Таңдау медицинада жұмыс істеуге қарсы көрсетілудің мотивациясы мен болмауына негізделген.', 'k-sibi-aynaldyru-mak-satynda-eresekterge-arnalg-an-test', 'CimocSr14Z.jpg', 1, NULL, NULL);
