INSERT INTO `professions` (`id`, `title`, `description`, `category_id`, `orentation_id`, `created_at`, `updated_at`) VALUES
(1, 'Акушер', 'Акушерами называют средний медицинский персонал, который оказывает помощь женщинам во время родов. Специалист может как самостоятельно принимать роды, так и ассистировать врачу.', 1, 3, NULL, NULL),
(2, 'Санитар', 'Санитар осуществляет обслуживание и уборку мед. помещений, выполняет обработку инструмента, ухаживает за пациентами. Относится к младшему медицинскому персоналу, для работы не нужно профильное образование.', 1, 3, NULL, NULL),
(3, 'Врач-физиотерапевт', 'Врач-физиотерапевт (От греч. phýsis​ – природа) — специалист по лечению различных заболеваний с помощью методов физиотерапии.', 1, 3, NULL, NULL),
(4, 'Программист C#', 'Программист C# специализируется на разработке веб-приложений, десктопных и кроссплатформенных приложений, игр, облачных сервисов, приложений для устройств, работающих на мобильной ОС Windows Phone, других программных продуктов.', 2, 2, NULL, NULL),
(5, 'Архитектор баз данных', 'Архитектор баз данных — опытный технический специалист, который руководит целой группой сотрудников. Он занимается выбором технологии для хранения данных, созданием и оптимизацией запросов, составляет план разработки и ТЗ для подчиненных, может выполнять проектирование и оптимизацию БД, следит за безопасностью БД.', 2, 2, NULL, NULL),
(9, 'Fullstack дизайнер', 'Fullstack дизайнер — это многозадачный специалист, который может совмещать дизайн и разработку. Он общается с заказчиком, контролирует процесс разработки, исправляет ошибки, хорошо знает о front-end и back-end.', 3, 5, NULL, NULL),
(10, 'Дизайнер интерфейсов', 'Дизайнер интерфейсов — это специалист, создающий удобный интерфейс для той или иной площадки, предугадывая действия ее посетителей. Он обеспечивает простую, понятную и приятную навигацию сайта, в результате чего потенциальный клиент не запутается в лабиринте ссылочной массы и страниц.', 3, 5, NULL, NULL),
(11, 'Декоратор', 'Декоратор (от англ. «decorate» — «украшать») — специалист по художественному оформлению и украшению интерьеров помещений: частных домов, театральных спектаклей, съемок, банкетных залов в ресторанах, офисов, бизнес-центров с использованием архитектурных, графических средств, живописи, освещения и постановочной техники.', 3, 5, NULL, NULL),
(12, 'Видеограф', 'Видеограф занимается съемкой различных событий, используя фото- и видеотехнику. Он работает на свадьбах, корпоративах, детских утренниках и других мероприятиях, занимается не только съемкой, но и монтажом, обработкой видео.', 4, 5, NULL, NULL),
(13, 'Актер', 'Актер (от фр. acteur, от лат. actor — исполнитель; женский род - актриса) - это профессиональный исполнитель разноплановых ролей в спектаклях, кино, рекламных роликах, видеоклипах.', 4, 5, NULL, NULL),
(14, 'Инженер-механик', 'Инженер-механик (mechanical engineer) — это специалист, который занимается проектированием, конструированием и эксплуатацией механического оборудования, машин, аппаратов в различных сферах производства и народного хозяйства.', 5, 2, NULL, NULL),
(15, 'Инженер по стандартизации, метрологии и сертификации', 'Инженер по стандартизации, метрологии и сертификации — это специалист в области обеспечения и оценки качества продукции, а также контроля за условиями эксплуатации технических средств (приборов, оборудования), закрепления в стандартах и нормативах правил для достижения экономии ресурсов при соблюдении безопасности производства.', 5, 2, NULL, NULL),
(16, 'Инженер-энергетик', 'Инженер-энергетик — это специалист, который занимается разработкой, производством и эксплуатацией систем теплового и энергетического обеспечения.', 5, 2, NULL, NULL),
(17, 'Инспектор по авиационной безопасности', 'Ответственность за безопасные, спокойные и своевременные авиаперелеты несет инспектор по авиационной безопасности. Он досматривает грузы, пассажиров, воздушные суда, предупреждая перевозку запрещенных веществ, террористические акты.', 6, 3, NULL, NULL),
(18, 'Машинист поезда (Машинист локомотива)', 'Машинист поезда – железнодорожник, управляющий пассажирскими и грузовыми поездами, электропоездами различных сообщений и назначения. Машинист ведёт поезд, управляя локомотивом.', 6, 3, NULL, NULL),
(19, 'Риск-менеджер', 'Риск-менеджеры, составляющие стратегии, проводящие исследования, идентификацию, контроль разных видов рисков, помогают избежать серьезных экономических и репутационных потерь компаниям. Специалисты позволяют сохранить доходность и эффективность бизнес-процессов в любых ситуациях.', 7, 3, NULL, NULL),
(20, 'Офис-менеджер', 'Офис-менеджер — это человек, который закупает канцелярские товары, ведет документацию, контролирует состояние техники, обеспечивая текущую работу офиса.', 7, 3, NULL, NULL),
(21, 'Художник-дизайнер телеэфира (ТВ-дизайнер)', 'Художник-дизайнер телеэфира – специалист, отвечающий за графическое оформление телевизионных каналов.', 8, 5, NULL, NULL),
(22, 'Радиожурналист', 'Радиожурналист – корреспондент, редактор , ведущий эфира, работающий на радио.', 8, 5, NULL, NULL),
(23, 'Тележурналист', 'Тележурналист - журналист (корреспондент, редактор и т.д), работающий на телевидении.', 8, 5, NULL, NULL),
(24, 'Телеведущий', 'Телевизионный ведущий - это сотрудник телевизионного канала, который ведет программы (новостные, развлекательные, спортивные и пр.)', 8, 5, NULL, NULL),
(25, 'Антрополог', 'Антрополог - ученый, специализирующийся на изучении человека как биологического вида.', 9, 4, NULL, NULL),
(26, 'Археолог', 'Археолог - это историк, изучающий быт и культуру древних людей по различным артефактам.', 9, 4, NULL, NULL),
(27, 'Эколог', 'Эколог (инженер-эколог) - специалист, занимающийся анализом ситуации и разработкой мер для уменьшения существующего и возможного вреда природе. Эколог выявляет причины катаклизмов природы и разрабатывает возможности снижения воздействия людского фактора на окружающую среду.', 9, 1, NULL, NULL),
(28, 'Маркетолог-аналитик', 'Маркетолог-аналитик — вид маркетолога, который собирает и анализируюет информацию о рынке товаров и услуг, конкурентах и поведении потребителей, успешность маркетинговой стратегии компании-заказчика. Эта профессия одна из самых престижных и ответственных в своем сегменте.', 10, 3, NULL, NULL),
(29, 'Маркетолог по продукту', 'Маркетолог по продукту — специалист, отвечающий за создание инновационного товара или услуги, улучшение характеристик старых линеек товаров. Специалист отвечает за ассортимент компании, анализирует рынок и требования к продуктам.', 10, 3, NULL, NULL),
(38, 'Налоговый инспектор', 'Налоговый инспектор — сотрудник налоговой инспекции, в обязанности которого входит проведение камеральных и выездных налоговых проверок, анализ результатов, применение финансовых санкций к нарушителям.', 11, 4, NULL, NULL),
(39, 'Специалист по бюджетированию', 'Специалист по бюджетированию – это сотрудник, отвечающий за контроль финансовых потоков целой компании, определенных структурных подразделений. Он составляет бюджет на заданный период, контролирует его выполнение (затраты, вложения, соблюдение планов), анализирует различные данные, осуществляет учет.', 11, 4, NULL, NULL),
(40, 'Финансовый консультант', 'Финансовый консультант – это специалист, занятый консультированием в области финансовой политики, который может работать как самостоятельно, так и в штате фирмы.', 11, 4, NULL, NULL),
(41, 'Бухгалтер', 'Профессия бухгалтера очень востребована: бухгалтер есть в каждой организации. У бухгалтеров, как правило, нормированный рабочий день. Бухгалтер может работать удаленно или подрабатывать, ведя бухгалтерию мелких предприятий. Профессия бухгалтера престижна: на предприятиях бухгалтер относится к управленческому персоналу. Это работа в офисе. ', 11, 4, NULL, NULL),
(42, 'Судья', 'Судья — это вершитель правосудия; человек, наделённый судебной властью, за которым остаётся последнее слово в процессе принятия судебных решений.', 12, 3, NULL, NULL),
(43, 'Прокурор', 'Прокурор (от лат. procurare — управлять, ведать чем-либо, заботиться) — это лицо, уполномоченное представлять обвинение в суде, а также следить за соблюдением законности практически во всех сферах жизни страны. И всё это — от имени государства.', 12, 3, NULL, NULL),
(44, 'Юрист', 'Юрист (лат. jus, в переводе — право) это специалист, получивший юридическое образование в сфере юридических наук и правоведения.', 12, 3, NULL, NULL),
(45, 'Психолог', 'Психолог (др.-греч. psycho — душа; logos — знание), (англ. — psychologist) — специалист в области психологии, который занимается изучением душевного состояния и законов коррекции поведения человека, используя эти знания для оказания помощи в разрешении личностных проблем, адаптации к окружающему миру, улучшению психологического климата в семьях и коллективах.', 13, 3, NULL, NULL),
(46, 'Школьный учитель', 'Учитель – профессия в сфере образования, которая заключается в обучении и воспитании молодого поколения с целью успешного и быстрого вхождения его в общественную жизнь. ', 13, 3, NULL, NULL),
(47, 'Прораб', 'Прораб (сокращение от словосочеетания «производитель работ») — это должность руководителя среднего звена на стройке, своеобразного посредника между рабочими и их руководством в строительной компании. Если инженер-строитель руководит строительной площадкой в целом, то прораб отвечает за ее конкретный участок.', 14, 2, NULL, NULL),
(48, 'Инженер-строитель', 'Инженер-строитель – это специалист, который занимается планированием строительных и ремонтных работ, а также руководит ими.\r\n\r\n', 14, 2, NULL, NULL),
(49, 'Архитектор', 'Архитектор (от др.-греч. αρχι - главный, старший и др.-греч. τέκτων - плотник, строитель) — специалист, осуществляющий архитектурное проектирование, включая разработку объемно-планировочных и интерьерных решений. Архитектурное проектирование - это организация архитектурной среды, включающее в себя проектирование зданий, в том числе разработку интерьерных и объемно-планировочных решений.', 14, 5, NULL, NULL),
(50, 'Шеф-повар', 'Шеф-повар возглавляет процесс приготовления пищи на профессиональных кухнях, его слово — закон. Он разрабатывает меню, подает заявки на необходимые продукты, следит за чистотой и порядком на кухне.', 15, 3, NULL, NULL),
(51, 'Технолог общественного питания', 'Технолог общественного питания — это специалист по разработке, производству и контролю за качеством кулинарной и кондитерской продукции на предприятиях общественного питания.\r\n\r\n', 15, 3, NULL, NULL),
(52, 'Повар', 'Повар — это специалист по приготовлению пищи. Хорошего повара иногда называют волшебником, ведь он может из самых обычных продуктов приготовить настоящий шедевр, который доставит радость и наслаждение людям.', 15, 3, NULL, NULL),
(53, 'Топограф', 'Топограф занимается отображением местности на планах, измерением поверхности Земли, съемкой рельефа, ориентированием. Планы и карты, созданные топографом, используются в строительстве, геодезии, разработке месторождений, военном деле и других сферах.', 16, 2, NULL, NULL),
(54, 'Инженер-геолог', 'Инженер-геолог занимается инженерно-геологическими изысканиями в строительстве, при реконструкции зданий и сооружений и отвечает за устойчивость зданий. В настоящее время, когда строительство зданий и сооружений идет полным ходом, эта профессия особенно важна и пользуется повышенным спросом.', 16, 2, NULL, NULL),
(55, 'Гид (Гид-экскурсовод)', 'Гид (Гид-экскурсовод) - человек, сопровождающий туристов и показывает им достопримечательности страны, города и т.п.; экскурсовод, проводник-профессионал.', 17, 3, NULL, NULL),
(56, 'Управляющий отелем', 'Управляющий отелем — верхняя ступень в карьерной лестнице персонала гостиницы. Он руководит всеми подразделениями отеля: службами по бронированию мест, приему и размещению гостей, организации телефонной и информационной службы, сервисными подразделениями, рестораном, салонами красоты. Управляющий должен обеспечить постояльцам комфортные условия пребывания под крышей отеля и желание вернуться вновь, владельцу отеля - высокий доход, а служащим — удовольствие от работы под его началом. Скоординировать всё это на должном уровне не так просто.', 17, 3, NULL, NULL),
(57, 'MICE-менеджер - Менеджер по деловому туризму', 'MICE-менеджер — менеджер по деловому туризму - менеджер по организации деловых встреч, поездок  и корпоративных мероприятий.', 17, 3, NULL, NULL),
(58, 'Фелинолог', 'Фелинолог — специалист по домашним кошкам.\r\n\r\n', 18, 1, NULL, NULL),
(59, 'Пчеловод (Пасечник)', 'Пчеловод - специалист по разведению пчел, их содержанию и получению продуктов пчеловодства.', 18, 1, NULL, NULL),
(60, 'Фотомодель', 'Фотомодель – человек, который принимает участие в рекламных съемках, может являться лицом коллекций, бендов, проектов. Работает с фотографами, визажистами, дизайнерами в сфере красоты.', 19, 5, NULL, NULL),
(61, 'Фотограф-стилист', 'Фотограф-стилист — это специалист, создающий атмосферу будущей фотографии, сохраняющий фотообразы и подчеркивающий природную красоту модели.', 19, 5, NULL, NULL),
(62, 'Брокер', 'Брокер является узкими специалистом, который выполняет роль посредника между участниками сделки, представляет интересы своего заказчика. Наиболее распространены биржевые брокеры, которые работают на рынке ценных бумаг.', 20, 3, NULL, NULL),
(63, 'Менеджер по продажам', 'Менеджер по продажам – это сотрудник компании, занимающийся реализацией товаров и услуг. ', 20, 3, NULL, NULL),
(64, 'Тренер по фитнесу (Фитнес-инструктор)', 'Тренер по фитнесу (англ. fitness, от глагола to fit – соответствовать, быть в хорошей форме.) — инструктор, руководящий тренировками, следящий за правильностью выполнения физических упражнений.', 21, 3, NULL, NULL),
(65, 'Спортивный тренер', 'Спортивный тренер - специалист по воспитанию спортсменов и подготовке их к соревнованиям.', 21, 3, NULL, NULL),
(66, 'Швея', 'Швея — специалист, в обязанности которого входит создание разного рода швейных изделий (одежда, обувь, изделия из кожи, чехлы, головные уборы и другие аксессуары) с использование специализированного оборудования.', 22, 5, NULL, NULL),
(67, 'Сборщик мебели', 'Сборщик мебели – специалист по сборке и разборке разных видов мебели.', 22, 5, NULL, NULL),
(68, 'Полицейский', 'Полицейский — человек, на которого государство возлагает обязанности по охране правопорядка. Должность относится к младшему начальствующему составу в кинологической службе, ППС и т. д. Работа сложная и очень опасная, но без полицейских мирные граждане не могут спокойно существовать.', 23, 3, NULL, NULL),
(69, 'Пожарный', 'Пожарный – сотрудник пожарной охраны, занимающийся тушением пожаров и спасением людей. Работа пожарного. Курсы, колледжи для подготовки пожарных. Пожарный также называется бойцом пожарной команды. И более романтический вариант – огнеборец.', 23, 3, NULL, NULL),
(70, 'Архивист (он же Архивариус)', 'Архивист — это хранитель или сотрудник архива, отвечающий за систематизацию и учет документов, доверенных на хранение.', 24, 4, NULL, NULL),
(71, 'Референт-переводчик', 'Референт-переводчик — это переводчик и личный помощник руководителя по важным вопросам.', 24, 3, NULL, NULL),
(72, 'Сварщик', 'Сварщик – специалист по металлу, который соединяет металлические детали в сложные конструкции при помощи электрической сварки.', 25, 2, NULL, NULL),
(73, 'Токарь', 'Токарь - специалист по обработке резаньем вращающихся заготовок или вращающегося режущего инструмента по обработке дерева, металла, пластмассы, смолы и т.д.', 25, 2, NULL, NULL),
(74, 'Социальный работник', 'Социальные работники оказывают всестороннюю помощь людям, взаимодействуя преимущественно с незащищенными категориями граждан. Они работают с беженцами, детьми-сиротами, пенсионерами, помогая им получить социальную, правовую, материальную поддержку.', 26, 3, NULL, NULL),
(75, 'Лингвист', 'Лингвист (языковед) — это специалист по лингвистике (языкознанию, языковедению), предметом исследования которого являются история образования и развития языков, их структура и характерные особенности.', 27, 3, NULL, NULL),
(76, 'Переводчик', 'Переводчик – общее понятие специалистов, занимающихся переводом устной или письменной речи с одного языка на другой. ', 27, 3, NULL, NULL),
(77, 'Мэппинг-режиссер', 'Мэппинг-режиссер – это талантливый специалист, занимающийся созданием феерических 3D-проекции для различных поверхностей. Он должен быть технически грамотным человеком, имеющим хорошо развитую фантазию и креативность.', 28, 5, NULL, NULL),
(80, 'Таможенный декларант', 'Таможенный декларант занимается созданием и оформлением документов, необходимых для импорта или экспорта товаров, взаимодействием с представителями таможенных пунктов. Специалист работает с физическими и юридическими лицами, являясь незаменимым сотрудником.', 29, 3, NULL, NULL),
(81, 'Специалист по ВЭД', 'Специалист, работающий в отделе ВЭД, курирует все операции, связанные с внешнеэкономической деятельностью фирмы. По сути, это экономист, отвечающий за оптимизацию закупок или продаж (экспортно-импортных операций).\r\n\r\n', 29, 3, NULL, NULL),
(82, 'Дипломат', 'Дипломат — работник ведомства внешних отношений, уполномоченный правительством для отношений с иностранными государствами, международными организациями и их представителями.', 30, 3, NULL, NULL),
(83, 'Политолог', 'Политолог – это специалист, который изучает политику как особую область жизни людей, связанную с властными отношениями, и анализирует события политической жизни.\r\n\r\nПолитолог – это специалист, который изучает политику как особую область жизни людей, связанную с властными отношениями, и анализирует события политической жизни.\r\n\r\n', 30, 3, NULL, NULL),
(84, 'Селекционер', 'Селекционер — ученый, занимающийся улучшением имеющихся и выводом новых видов животных, растений и других живых организмов, обладающих свойствами, полезными для человека или помогающими более эффективно адаптироваться к условиям окружающей среды.', 31, 1, NULL, NULL),
(85, 'Агроном', 'Агроном – специалист в области земледелия, обладающий всесторонними знаниями в области выращивания и сбора урожая растений.', 31, 1, NULL, NULL),
(86, 'Риeлтор', 'Риелтор - специалист, который занимается операциями с недвижимостью.', 32, 3, NULL, NULL),
(87, 'Эксперт по оценке собственности', 'Эксперт по оценке собственности - это специалист, определяющий реальную стоимость различных объектов:\r\n\r\nнедвижимости;\r\nбизнеса (организаций);\r\nинвестиционных проектов;\r\nактивов — финансовых и нематериальных;\r\nтранспортных средств;\r\nинтеллектуальной собственности;\r\nценных бумаг и др.', 32, 3, NULL, NULL),
(88, 'Мулла (Имам)', 'Слово «Мулла» в переводе с арабского языка означает «господин, владыка». В современном мире мулла — это обобщенное звание мусульманского религиозного деятеля, который руководит молитвой при мечети или является духовной главой мусульманской общины. Муллой называют имамов и их заместителей в мечетях. «Имам» переводится с арабского слова «амма» как «пример для подражания», «предводитель». Имам — не профессия в общепринятом смысле этого слова. Руководить молитвой в сельской или квартальной мечети может любой мусульманин, который пользуется наибольшим авторитетом, имеет элементарное исламское образование и более других знает правила совершения молитвенных ритуалов. В крупных соборных мечетях проповедуют имамы, получившие высшее богословское образование.', 33, 3, NULL, NULL),
(89, 'Православный священник', 'Православный священник — в общеупотребительном (нетерминологическом) значении — служитель религиозного культа.\r\n', 33, 3, NULL, NULL),
(90, 'Буровик', 'Буровик — это специалист, управляющий с поверхности земли углублением забоя скважины, креплением пород на стенках скважины, созданием своеобразной опалубки из высокопрочных труб и цементного раствора, вскрытием продуктивных залежей и вызовом притока нефти и газа, ликвидацией осложнений и аварий. Буровик — профессия настоящих мужчин.', 34, 2, NULL, NULL),
(91, 'Шахтёр', 'Шахтёр – общее название рабочих профессий, связанных с работой в шахте.\r\n\r\n\r\n\r\n\r\n\r\n\r\nШахтёр – рабочий, добывающий полезные ископаемые подземным способом, т.е. в шахте.\r\n\r\nК шахтёрам относятся:\r\nГорнорабочий очистного забоя (ГРОЗ) – одна из двух основных шахтных специальностей. Этот рабочий (на профессиональном жаргоне – добычник) непосредственно добывает уголь в очистном забое (лаве)*. Одна из самых высокооплачиваемых профессий в шахте.\r\n\r\nДобычник  выполняет целый комплекс работ по очистной выемке полезного ископаемого, укрепляет своды забоя, устанавливает распорные стойки, бурит скважины и пр.', 34, 2, NULL, NULL),
(92, 'Специалист по техносферной безопасности', 'Специалист по техносферной безопасности — актуальная и необходимейшая профессия в современном мире. Его миссию можно сравнить с божественным промыслом: если Бог создал мир, то специалист по техносферной безопасности призван его сохранить.', 35, 1, NULL, NULL);
